<template>
  <transition name="fade">
    <div v-show="isShow" :style="{ 'z-index': zIndex }" class="mask common-mask" @click="clickHidden()">
      <div v-if="isLoading" class="wrap-loading tc">
        <i class="loading-bar"></i>
        <p class="pt10">{{ $i18n("加载中") }}...</p>
      </div>
    </div>
  </transition>
</template>

<script type="text/ecmascript-6">

import IndexTools from '../z-index';

export default {
	data () {
		return {
			isShow: false,
			uid: -1,
			isCloseByClick: false,
			clickCallback: null,
			zIndex: 900,

			isLoading: false
		}
	},
	methods: {

		clickHidden () {
			if (this.clickCallback) {
				this.clickCallback(this);
			}
		},
		hidden () {
			this.isShow = false
		},
		show () {

			this.isLoading = false;
			this.isShow = true;

			this.zIndex = IndexTools.getCurZIndex();
		},
		loading () {
			this.show();

			this.isLoading = true;
		}
  }
}
</script>

<style lang="scss" rel="stylesheet/scss">
.common-mask {
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: 900;
  background: rgba(17, 17, 17, 0.5);

  .wrap-loading {
    position: fixed;
    left: 50%;
    top: 50%;
    width: 100px;
    height: 90px;
    transform: translate(-50%, -50%);
    background: rgba($color: $skin-font5, $alpha: 0.7);
    border-radius: 5px;
    color: $skin-font1;

  }
  .loading-bar {
    width: 30px;
    height: 30px;
    display: block;
    background: url(data:image/svg+xml;base64,) 50% no-repeat;
    background-size: 100% 100%;
    margin: 16px auto 0;
    animation: rotateFn 1500ms linear infinite;
  }
}
@keyframes rotateFn {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotateZ(360deg);
  }
}
.fade-enter-active,
.fade-leave {
  transition: opacity 0.3s;
}

.fade-enter,
.fade-leave-active {
  opacity: 0;
}
</style>
